			{
				stMag.LastBlock = (double)pIndex->nHeight;
			}
			stMag.entries++;
			stMag.payments += pIndex->nResearchSubsidy;
			stMag.interestPayments += pIndex->nInterestSubsidy;

			AdjustTimestamps(stMag,(double)pIndex->nTime,pIndex->nResearchSubsidy);
