		std::string pub_key_prefix = vCommand[4];
		std::string bhrn =           vCommand[5]; 
		std::string grid_pass =      vCommand[6];
		std::string grid_pass_decrypted = AdvancedDecryptWithSalt(grid_pass,sboinchashargs);
		
