{
    CTransaction tx;
    tx.hashBoinc = "<MT>beacon</MT><MK>363d6c820aef2dbbe082768b40feed0d</MK>"
    "<MV></MV>"
    "<MA>A</MA>";
    tx.nTime= tx.nLockTime= pindexBest->nTime;
    BOOST_CHECK(true == VerifyBeaconContractTx(tx));
}

